# The views used below are normally mapped in django.contrib.admin.urls.py
# This URLs file is used to provide a reliable view deployment for test purposes.
# It is also provided as a convenience to those who want to deploy these URLs
# elsewhere.

from django.conf.urls import url
from django.contrib.auth import views

urlpatterns = [
    url(r'^login/$', views.login, name='auth_login'),
    url(r'^logout/$', views.logout, name='auth_logout'),
    url(r'^password_change/$', views.password_change, name='auth_password_change'),
    url(r'^password_change/done/$', views.password_change_done, name='auth_password_change_done'),
    url(r'^password_reset/$', views.password_reset, name='auth_password_reset'),
    url(r'^password_reset/done/$', views.password_reset_done, name='auth_password_reset_done'),
    url(r'^reset/(?P<uidb64>[0-9A-Za-z_\-]+)/(?P<token>[0-9A-Za-z]{1,13}-[0-9A-Za-z]{1,20})/$',
        views.password_reset_confirm, name='auth_password_reset_confirm'),
    url(r'^reset/done/$', views.password_reset_complete, name='auth_password_reset_complete'),
]
